const ADD_POST = 'ADD_POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE_NEW_POST_TEXT';

const prodileReducer = (state, action) => {

	switch (action.type) {
		case ADD_POST:
			let newPost = {
				id: 4,
				message: state.newPostText,
				likeCount: 50
			};
			state.posts.push(newPost);
			state.newPostText = '';
			return state;
		case UPDATE_NEW_POST_TEXT:
			state.newPostText = action.newText; return state;
	}

	if (action.type === 'ADD_POST') {
		let newPost = {
			id: 4,
			message: state.newPostText,
			likeCount: 50
		};

		state.posts.push(newPost);
		state.newPostText = '';

	} else if (action.type === UPDATE_NEW_POST_TEXT) {
		state.newPostText = action.newText;
	}

	return state;
}

export default prodileReducer;
