const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
let SET_USERS = 'SET_USERS';


let initialState = {
	users:
		[
			// { id: 1, fullName: 'Sergey', status: 'Junior', location: 'Tomsk', country: 'Russia', followed: false },

			// { id: 2, fullName: 'Dmitriy', status: 'Boss', location: 'Tomsk', country: 'Russia', followed: true },

			// { id: 3, fullName: 'Alex', status: 'Dranik', location: 'Tomsk', country: 'Russia', followed: true },
		]
}

const usersReducer = (state = initialState, action) => {
	switch (action.type) {
		case FOLLOW:
			return {
				...state,
				users: state.users.map(u => {
					if (u.id === action.userId) {
						return { ...u, followed: true }
					}
					return u;
				})
			};
		case UNFOLLOW:
			return {
				...state,
				users: state.users.map(u => {
					if (u.id === action.userId) {
						return { ...u, followed: false }
					}
					return u;
				})
			};
		case SET_USERS:
			return { ...state, users: [...state.users, ...action.users] }
		default:
			return state;
	}
};


export const followAC = (usersId) => ({ type: FOLLOW, usersId });
export const unFollowAC = (usersId) => ({ type: UNFOLLOW, usersId });
export const setUsersAC = (users) => { type: SET_USERS, users };

export default usersReducer;
